// src/components/Navbar.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Navbar.css';
import LoginForm from './LoginForm'; // Import the login form
import Modal from 'react-modal';     // Import Modal

Modal.setAppElement('#root');  // Required for accessibility

function Navbar() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <nav className="navbar">
      <h1>Art Gallery Marketplace</h1>
      <ul>
        <li><Link to="/artworks">Artworks</Link></li>
        <li><Link to="/artists/profile">Artist Profile</Link></li>
        <li><Link to="/reviews">Reviews</Link></li>
        <li><button onClick={openModal} className="login-btn">Login</button></li> {/* Open login modal */}
      </ul>

      {/* Modal for login form */}
      <Modal 
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Login Form"
        className="login-modal"
        overlayClassName="modal-overlay"
      >
        <button onClick={closeModal} className="close-modal-btn">&times;</button>
        <LoginForm /> {/* Display the login form inside the modal */}
      </Modal>
    </nav>
  );
}

export default Navbar;
