// src/components/Purchases.js

import React, { useEffect, useState } from 'react';
import '../styles/Purchase.css'; // Import your CSS for styling

function Purchases() {
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    // Fetch purchases from the backend
    fetch(`https://art-gallery-backend-2-1.onrender.com/purchases`) // Replace with your actual endpoint
      .then(res => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then(data => setPurchases(data))
      .catch(err => console.error('Error fetching purchases:', err));
  }, []);

  return (
    <div className="purchase-container">
      <h1>Your Purchases</h1>
      {purchases.length === 0 ? (
        <p>You have not made any purchases yet.</p>
      ) : (
        <ul>
          {purchases.map(purchase => (
            <li key={purchase.id}>
              <h2>{purchase.title}</h2>
              <p>Description: {purchase.description}</p>
              <p>Price: ${purchase.price}</p>
              <img src={purchase.image_url} alt={purchase.title} />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Purchases;
